        var knobX = Math.round(ratio * (trackW - 8)) + 4;
        var knobR = 8;

        // Track bg
        L2_Theme.fillRoundRect(c, 0, trackY, trackW, trackH, 3, L2_Theme.borderDark);
        // Track fill
